import React, { useRef, useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
     faPlay,
     faAngleDoubleLeft,
    faAngleDoubleRight,
 } from '@fortawesome/free-solid-svg-icons'
const Player = ({currentSong, isPlaying, setIsPlaying}) => {


    const audioRef = useRef(null)
    const playMediaHandler = () => {
     if(isPlaying){
         audioRef.current.pause();
         setIsPlaying(!isPlaying)
     }   else {
         audioRef.current.play();
         setIsPlaying(!isPlaying)
     }
    }
    const [songInfo, setSongInfo ] = useState({
        currentTime: null,
        duration: null
    })
    const setInputValue = (time) => {
        return(
            Math.floor(time / 60) + ":" + ("0" + Math.floor(time ) )
        )
    }
    const songInfoHandler = (e) => {
        const current = e.target.currentTime
        const duration = e.target.duration
        setSongInfo({...songInfo, currentTime: current, duration})
    }
    return (
        <div className="player-container">
            <div className="time-control">
                <span className="start-time">00:00</span>
                <input type="range" name="" id="song-length" min="0" max="100" value="0" />
                <span className="end-time">00:00</span>
            </div>
            <div className="play-control">
                <FontAwesomeIcon 
                size="2x"
                 className="skipBack"
                  icon={faAngleDoubleLeft}
                  />
                <FontAwesomeIcon 
                size="2x"
                 className="play"
                  icon={faPlay}
                    onClick={playMediaHandler}
                  />
                <FontAwesomeIcon 
                size="2x"
                 className="skipForward"
                  icon={faAngleDoubleRight}
                  />
            </div>
            <audio 
                onTimeUpdate={songInfoHandler}
                src={currentSong.audio} 
                ref={audioRef}>
            </audio>
        </div>
    )
}

export default Player
